template.content:
package ${packageName}<#if moduleName != "">.${moduleName}</#if>.config;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;

import com.changan.jsonmeta.ResourceMetaManager;
import com.fasterxml.jackson.databind.ObjectMapper;

/**
 * ResourceMetadataConfiguration
 *
 * @author TitanCode2
 *
 */
@Configuration
public class ResourceMetadataConfiguration {

  @Autowired
  private ObjectMapper objectMapper;

  @Bean
  public ResourceMetaManager resourceMetaManager() {
    return new ResourceMetaManager(objectMapper);
  }

}
