SELECT f.id as id, f.function_name as functionName, f.code as code, f.url as url, f.is_menu as isMenu, f.description as description, f.enabled as enabled, f.parent_id as parentId, f.image1_id as image1Id, f.image2_id as image2Id, f.created_at as createdAt, f.updated_at as updatedAt, f.created_by as createdBy, f.updated_by as updatedBy FROM hotpot_function f INNER JOIN ( SELECT DISTINCT (rf.function_id) FROM hotpot_role_function rf INNER JOIN ( SELECT ru.* FROM hotpot_role_user ru INNER JOIN hotpot_role r ON ru.role_id = r.id AND r.del_flag = '0' WHERE ru.party_id in
#{item}
) ru ON ru.role_id = rf.role_id ) r ON r.function_id = f.id WHERE f.enabled = 'Y' ORDER BY CODE
